// api.js

const axios = require("axios");

const baseURL = "http://www.aladin.co.kr/ttb/api/ItemSearch.aspx";
const apiKey = "ttbcgn37311506001";

// 검색 결과를 가져오는 함수
async function searchBooks(keyword) {
  try {
    const response = await axios.get(`${baseURL}?apikey=${apiKey}&query=${encodeURIComponent(keyword)}`);
    return response.data;
  } catch (error) {
    console.error("API 호출 중 오류가 발생했습니다:", error);
    throw error;
  }
}

module.exports = {
  searchBooks,
};
