import { styled } from "styled-components";

export const BestBooksContainer = styled.div`
  width: 100%;
  height: 1700px;
  margin-bottom: 50px;
  letter-spacing: -1.2px;
`;

export const Text = styled.div`
  height: 35px;
  padding: 10px;
  font-size: 20px;
  font-weight: bold;
  color: #333;
`;

export const ImageContainer = styled.div`
  width: 100%;
  padding: 10px;
  display: flex;
`;

export const Book = styled.img`
  width: 137px;
  height: 200px;
  margin-right: 20px;
`;

export const BookTitle = styled.div`
  width: 135px;
  margin-top: 10px;
  line-height: 18px;
  font-weight: bold;
  color: #333;
`;

export const BookWriter = styled.div`
  width: 135px;
  margin-top: 7px;
  font-size: 13px;
  font-weight: bold;
  color: #6f6f6f;
`;
