import React, { useEffect, useRef } from "react";
import { styled } from "styled-components";
import { Swiper, SwiperSlide } from "swiper/react";
import { Autoplay } from "swiper";
import "./swiper-bundle.css";

import Book1 from "../images/books/노르웨이의숲.jpg";
import Book2 from "../images/books/달러구트꿈백화점.jpg";
import Book3 from "../images/books/데미안.jpg";
import Book4 from "../images/books/멋진신세계.jpg";
import Book5 from "../images/books/어린왕자.jpg";
import Book6 from "../images/books/인간실격.jpg";

export const BooksContainer = styled.div`
  width: 100%;
  background-color: white;
  padding: 30px;
`;

export const Inner = styled.div`
  width: 97%;
  height: 700px;
  margin: 0 auto;
`;

export const TextBox = styled.div`
  width: 500px;
  height: 200px;
  margin: 0 auto;
  margin-top: 80px;
  text-align: center;
  .text1 {
    height: 50%;
    font-size: 50px;
    font-weight: 600;
    color: #333;
  }
  .text2 {
    height: 50%;
    font-size: 23px;
    color: #767676;
  }
`;

export const CanvasContainer = styled.div`
  height: 390px;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const ImageBox = styled.div`
  height: 390px;
`;

export const Img = styled.img`
  width: 130px;
  height: 180px;
`;

