  return (
    <BooksContainer>
      <Inner>
        <TextBox>
          <div className="text1">책 속 세상</div>
          <div className="text2">
            여러분이 좋아하는 책들이 회전하고 있습니다.
          </div>
        </TextBox>
        {/* <CanvasContainer>
          <canvas ref={canvasRef} width="800" height="390" />
        </CanvasContainer> */}
        <ImageBox>
          <Swiper
            spaceBetween={20}
            slidesPerView="6"
            centeredSlides={true}
            loop={true}
            loopAdditionalSlides={1}
            speed={1000}
            autoplay={{
              deley: 0,
              disableOnInteraction: false,
            }}
            navigation={true}
            modules={[Autoplay]}
          >
            <div class="swiper-wrapper">
              <SwiperSlide class="swiper-wrapper">
                <Img src={Book1} alt="노르웨이의 숲" />
              </SwiperSlide>
