                <Img src={Book5} alt="어린왕자" />
              </SwiperSlide>
              <SwiperSlide>
                <Img src={Book6} alt="인간실격" />
              </SwiperSlide>
            </div>
          </Swiper>
        </ImageBox>
      </Inner>
    </BooksContainer>
  );
};

export default Books;
