import axios from "axios";

export const BaseURL = "http://www.aladin.co.kr/ttb/api/ItemSearch.aspx";
export const ApiKey = "ttbcgn37311506001";

export async function searchBooks(searchTerm, category) {
  try {
    const url = `${BaseURL}?ttbkey=${ApiKey}&Query=${encodeURIComponent(
      searchTerm
    )}&QueryType=${category}&MaxResults=10&Start=1&SearchTarget=Book&Output=XML&Version=20131101`;
    const response = await axios.get(url);
    return response.data;
  } catch (error) {
    console.error("API 호출 중 오류가 발생했습니다:", error);
    throw error;
  }
}
