import axios from "axios";

const baseURL = "http://www.aladin.co.kr/ttb/api/ItemSearch.aspx";
const apiKey = "ttbcgn37311506001";

async function searchBooks(searchTerm, category) {
  try {
    const url = `${baseURL}?ttbkey=${apiKey}&Query=${encodeURIComponent(
      searchTerm
    )}&QueryType=${category}&MaxResults=10&Start=1&SearchTarget=Book&Output=XML&Version=20131101`;
    const response = await axios.get(url);
    return response.data;
  } catch (error) {
    console.error("API 호출 중 오류가 발생했습니다:", error);
    throw error;
  }
}

export default searchBooks;
