import React, { useState } from "react";
import axios from "axios";
import { baseURL, apiKey } from "../components/api";

const Search = () => {
  const [category, setCategory] = useState("");
  const [searchTerm, setSearchTerm] = useState("");

  const handleCategoryChange = (e) => {
    setCategory(e.target.value);
  };

  const handleSearchTermChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearch = () => {
    const url = `${baseURL}?ttbkey=${apiKey}&Query=${searchTerm}&QueryType=${category}&MaxResults=10&Start=1&SearchTarget=Book&Output=XML&Version=20131101`;

    // API 호출
    axios
      .get(url)
      .then((response) => {
        // API 응답 처리
        console.log("API 응답:", response.data);
      })
      .catch((error) => {
        // 에러 처리
        console.error("API 호출 에러:", error);
      });
  };

  return (
    <div>
      <select value={category} onChange={handleCategoryChange}>
        <option value="">카테고리 선택</option>
        <option value="제목">제목</option>
        <option value="저자">저자</option>
        <option value="출판사">출판사</option>
      </select>
      <input type="text" value={searchTerm} onChange={handleSearchTermChange} placeholder="검색어 입력" />
      <button onClick={handleSearch}>검색</button>
    </div>
  );
};

export default Search;
